// dependency imports
import React, { useState, useRef, useEffect } from "react";
import { LocomotiveScrollProvider } from "react-locomotive-scroll";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  NavLink,
} from "react-router-dom";
import gsap from "gsap";
import SplitText from "./utils/Split3.min";
import AnimatedCursor from "react-animated-cursor";

// style imports
import "./styles/App.scss";
import "./styles/fonts.css";

// component imports
import Header from "./components/header/header";
import SectionMenu from "./components/workMenu/index.jsx";

// page imports
import Home from "./pages/home";
import About from "./pages/about";

import Project1 from "./pages/workProjects/projects";

// import Work from "./pages/work";
// import RouteWithSubRoutes from "./pages/work";

// route config is just an array of logical "routes"
// with `path` and `component` props, ordered the same
// way as inside a `<Switch>`.
export const routes = [
  // main
  { path: "/home", component: Home, exact: true, name: "Home" },
  {
    path: "/about",
    component: About,
    name: "About",
  },
  {
    path: "/work",
    component: Work,
    name: "Work",
    routes: [
      // work categories
      {
        path: "/work/ux",
        component: UxMenu,
        name: "UX",
      },
      {
        path: "/work/web",
        component: WebMenu,
        name: "Web",
      },
      {
        path: "/work/graphics",
        component: GraphicsMenu,
        name: "Graphics",
      },
      {
        path: "/work/java",
        component: JavaMenu,
        name: "Java",
      },
      {
        path: "/work/photo",
        component: PhotoMenu,
        name: "Photo",
      },
    ],
  },
  // projects
  {
    path: "/project1",
    component: Project1,
    name: "Photo",
  },
];

// all projects, with subititle, topic & path
const projects = [
  { h1: "bleached", h2: "things done", to: "/work", topics: ["graphics"] },
  { h1: "logo", h2: "", to: "/work", topics: ["graphics"] },

  {
    h1: "floodIt",
    h2: "things done",
    to: "/work",
    topics: ["java"],
  },
  {
    h1: "kruskal's maze",
    h2: "things done",
    to: "/work",
    topics: ["java"],
  },
  {
    h1: "portrait",
    h2: "things done",
    to: "/work",
    topics: ["photo"],
  },
  {
    h1: "landscape",
    h2: "things done",
    to: "/work",
    topics: ["photo"],
  },
  {
    h1: "wildlife",
    h2: "things done",
    to: "/work",
    topics: ["photo"],
  },
  {
    h1: "student hub 2.0",
    h2: "things done",
    to: "/work",
    topics: ["ux"],
  },
  {
    h1: "nonsense ui",
    h2: "things done",
    to: "/work",
    topics: ["ux"],
  },
  {
    h1: "google doodle",
    h2: "things done",
    to: "/work",
    topics: ["ux", "web"],
  },
  {
    h1: "banksy?",
    h2: "things done",
    to: "/work",
    topics: ["ux", "web"],
  },
  {
    h1: "portfolio",
    h2: "things done",
    to: "/work",
    topics: ["ux", "web"],
  },
];

// main app component
export default function App() {
  const containerRef = useRef(null);

  return (
    <>
      <LocomotiveScrollProvider
        options={{
          smooth: true,
          // ... available Locomotive Scroll instance options
        }}
        watch={
          [
            //...all the dependencies you want to watch to update the scroll
          ]
        }
        containerRef={containerRef}
      >
        <main data-scroll-container ref={containerRef}>
          <div className="main-container" id="app">
            {/* // <AnimatedCursor /> */}
            <Header />

            <div className="container">
              {routes.map((route, i, path, Component) => (
                <RouteWithSubRoutes key={i} {...route}></RouteWithSubRoutes>
              ))}
            </div>
          </div>
        </main>
      </LocomotiveScrollProvider>
    </>
  );
}

// main work menu
function Work({ routes }) {
  useEffect(() => {
    const split = new SplitText("#section-item", {
      type: "lines",
      linesClass: "lineChildren",
    });

    const line = new SplitText("#section-item", {
      type: "lines",
      linesClass: "lineParent",
    });

    gsap.to(split.lines, {
      duration: 0.5,
      y: 0,
      opacity: 1,
      stagger: 0.1,
      ease: "power2",
    });
  }, []);

  console.clear();
  const elApp = document.querySelector("#app");

  // What we need...
  // Mouse position
  // Currently hovered item

  document.body.addEventListener("mousemove", (event) => {
    console.log({
      x: event.clientX,
      y: event.clientY,
    });

    console.log(elApp);
    /* elApp.style.setProperty("--x", event.clientX);
    elApp.style.setProperty("--y", event.clientY); */
  });

  // Replay animation by hiding & showing the element again
  let el = document.body;
  el.addEventListener("click", function (e) {
    el.hidden = true;
    requestAnimationFrame(() => {
      el.hidden = false;
    });
  });

  // work testing

  return (
    <div className="workContainer" id="app">
      <div className="logoContainerSpacer logoHeadingSpacer">
        <ul>
          <li>
            <NavLink
              className="workLink"
              exact
              activeClassName="activeWorkLink"
              to="/work/"
              id="section-item"
            >
              all work
            </NavLink>
          </li>
          <li>
            <NavLink
              className="workLink"
              activeClassName="activeWorkLink"
              to="/work/ux"
              id="section-item"
            >
              ux
            </NavLink>
          </li>
          <li>
            <NavLink
              className="workLink"
              activeClassName="activeWorkLink"
              to="/work/web"
              id="section-item"
            >
              web
            </NavLink>
          </li>
          <li>
            <NavLink
              className="workLink"
              activeClassName="activeWorkLink"
              to="/work/graphics"
              id="section-item"
            >
              graphics
            </NavLink>
          </li>
          <li>
            <NavLink
              className="workLink"
              activeClassName="activeWorkLink"
              to="/work/java"
              id="section-item"
            >
              java
            </NavLink>
          </li>
          <li>
            <NavLink
              className="workLink"
              activeClassName="activeWorkLink"
              to="/work/photo"
              id="section-item"
            >
              photo
            </NavLink>
          </li>
        </ul>
      </div>

      <Switch>
        // section switch
        {routes.map((route, i) => (
          <RouteWithSubRoutes key={i} {...route} />
        ))}
        // all work list - change to featured later
        <div>
          <SectionMenu menuItems={projects} />
          {/* <a href="#" class="image-hover">
            <span class="title style2" data-title="G">
              G
            </span>
            <img src="/img/doodle/G.jpeg" alt="" />
          </a> */}
        </div>
      </Switch>
    </div>
  );
}

function GraphicsMenu() {
  return (
    <div className="sectionMenuContainer">
      <SectionMenu
        menuItems={projects
          .filter((project) => project.topics.includes("graphics"))
          .map((filteredProject) => filteredProject)}
      />
    </div>
  );
}

function JavaMenu() {
  return (
    <div className="sectionMenuContainer">
      <SectionMenu
        menuItems={projects
          .filter((project) => project.topics.includes("java"))
          .map((filteredProject) => filteredProject)}
      />
    </div>
  );
}

function PhotoMenu() {
  return (
    <div className="sectionMenuContainer">
      <SectionMenu
        menuItems={projects
          .filter((project) => project.topics.includes("photo"))
          .map((filteredProject) => filteredProject)}
      />
    </div>
  );
}

function UxMenu() {
  return (
    <div className="sectionMenuContainer">
      <SectionMenu
        menuItems={projects
          .filter((project) => project.topics.includes("ux"))
          .map((filteredProject) => filteredProject)}
      />
    </div>
  );
}

function WebMenu() {
  return (
    <div className="sectionMenuContainer">
      <SectionMenu
        menuItems={projects
          .filter((project) => project.topics.includes("web"))
          .map((filteredProject) => filteredProject)}
      />
    </div>
  );
}

// A special wrapper for <Route> that knows how to
// handle "sub"-routes by passing them in a `routes`
// prop to the component it renders.
function RouteWithSubRoutes(route) {
  return (
    <Route
      path={route.path}
      render={(props) => (
        // pass the sub-routes down to keep nesting
        <route.component {...props} routes={route.routes} />
      )}
    />
  );
}
